mapper.update:
UPDATE board
SET title =#{title}, thumbnail=#{thumbnail}, content=#{content}, updated_at=now(), start_at = #{start_at},
    finish_at=#{finish_at}, frequency=#{frequency}, numOfPeople=#{numOfPeople}, place=#{place}, min_age=#{min_age}, max_age =#{max_age}
WHERE id = #{id}

UPDATE board
SET is_deleted = true
WHERE id=#{board_id}
