mapper.select:
SELECT *
FROM post_reply
WHERE post_no = #{post_no}
  AND reply_no = #{reply_no}

SELECT post_no
FROM post_reply
WHERE post_no = #{post_no}
  AND reply_no = #{reply_no}

SELECT reply_no
FROM post_reply
WHERE post_no = #{post_no}
  AND reply_no = #{reply_no}

SELECT parent_reply_no
FROM post_reply
WHERE post_no = #{post_no}
  AND reply_no = #{reply_no}

SELECT content
FROM post_reply
WHERE post_no = #{post_no}
  AND reply_no = #{reply_no}

SELECT writer
FROM post_reply
WHERE post_no = #{post_no}
  AND reply_no = #{reply_no}

SELECT created_at
FROM post_reply
WHERE post_no = #{post_no}
  AND reply_no = #{replyNo}

mapper.update:
UPDATE post_reply
SET content = #{content}
WHERE post_no = #{post_no}
  AND reply_no = #{reply_no}

mapper.delete:
DELETE
FROM post_reply
WHERE post_no = #{post_no}
  AND reply_no = #{reply_no}
