mapper.select:
SELECT *
FROM post_reply
WHERE post_no = #{postNo}
  AND reply_no = #{replyNo}

SELECT post_no
FROM post_reply
WHERE post_no = #{postNo}
  AND reply_no = #{replyNo}

SELECT reply_no
FROM post_reply
WHERE post_no = #{postNo}
  AND reply_no = #{replyNo}

SELECT parent_reply_no
FROM post_reply
WHERE post_no = #{postNo}
  AND reply_no = #{replyNo}

SELECT content
FROM post_reply
WHERE post_no = #{postNo}
  AND reply_no = #{replyNo}

SELECT writer
FROM post_reply
WHERE post_no = #{postNo}
  AND reply_no = #{replyNo}

SELECT created_at
FROM post_reply
WHERE post_no = #{postNo}
  AND reply_no = #{replyNo}

mapper.update:
UPDATE post_reply
SET content = #{content}
WHERE post_no = #{postNo}
  AND reply_no = #{replyNo}

mapper.delete:
DELETE
FROM post_reply
WHERE post_no = #{postNo}
  AND reply_no = #{replyNo}
